package com.java.git;

public class PrintPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		  System.out.println("*");
	        for (int j = 0; j<=10; j++ ) {
	            String s = "*";
	            for(int k=0; k<=j; k++) {
	                s= s + "*";
	            }
	            System.out.println(s);

	        }
	    }
	}

